import axios from 'axios';
import toast from 'react-hot-toast';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';

// Create axios instance
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Token expired or invalid
      localStorage.removeItem('token');
      window.location.href = '/login';
      toast.error('Sitzung abgelaufen. Bitte erneut anmelden.');
    } else if (error.response?.status === 403) {
      toast.error('Keine Berechtigung f체r diese Aktion');
    } else if (error.response?.status === 500) {
      toast.error('Serverfehler. Bitte sp채ter erneut versuchen.');
    }
    
    return Promise.reject(error);
  }
);

// Helper functions for common requests
export const apiHelpers = {
  // Upload file
  uploadFile: async (file, endpoint) => {
    const formData = new FormData();
    formData.append('file', file);
    
    return api.post(endpoint, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
  },

  // Download file
  downloadFile: async (endpoint, filename) => {
    try {
      const response = await api.get(endpoint, {
        responseType: 'blob',
      });
      
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      toast.error('Fehler beim Download');
      throw error;
    }
  },

  // Paginated request
  getPaginated: async (endpoint, page = 1, limit = 20, params = {}) => {
    return api.get(endpoint, {
      params: {
        page,
        limit,
        ...params,
      },
    });
  },
};

export default api;
