import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../../services/api';
import toast from 'react-hot-toast';
import ShiftModal from '../../components/ShiftModal';

const ShiftManagement = () => {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const [shifts, setShifts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [selectedShift, setSelectedShift] = useState(null);

  useEffect(() => {
    fetchShifts();
  }, []);

  const fetchShifts = async () => {
    try {
      setLoading(true);
      const response = await api.get(`/events/${eventId}/shifts`);
      setShifts(response.data);
    } catch (error) {
      toast.error('Fehler beim Laden der Schichten');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateShift = () => {
    setSelectedShift(null);
    setShowCreateModal(true);
  };

  const handleUpdateShift = (shift) => {
    setSelectedShift(shift);
    setShowCreateModal(true);
  };

  const handleDeleteShift = async (shiftId) => {
    if (window.confirm('Möchten Sie diese Schicht wirklich löschen?')) {
      try {
        await api.delete(`/shifts/${shiftId}`);
        toast.success('Schicht gelöscht');
        fetchShifts();
      } catch (error) {
        toast.error('Fehler beim Löschen der Schicht');
      }
    }
  };

  return (
    <div>
      <h1>Schichtverwaltung für Event {eventId}</h1>
      <button onClick={() => navigate(`/events/${eventId}`)}>Zurück zum Event</button>
      <button onClick={handleCreateShift}>Neue Schicht erstellen</button>
      {loading ? (
        <p>Lade Schichten...</p>
      ) : (
        <ul>
          {shifts.map((shift) => (
            <li key={shift.id}>
              {shift.name} ({shift.start_time} - {shift.end_time})
              <button onClick={() => handleUpdateShift(shift)}>Bearbeiten</button>
              <button onClick={() => handleDeleteShift(shift.id)}>Löschen</button>
            </li>
          ))}
        </ul>
      )}
      <ShiftModal
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        onSave={fetchShifts}
        eventId={eventId}
        shift={selectedShift}
      />
    </div>
  );
};

export default ShiftManagement;
