import api from '../services/api';

const ShiftModal = ({ isOpen, onClose, onSave, eventId, shift }) => {
  const [formData, setFormData] = useState({
    name: '',
    startTime: '',
    endTime: '',
    requiredStaff: '',
    notes: '',
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (shift) {
      setFormData({
        name: shift.name,
        startTime: shift.start_time,
        endTime: shift.end_time,
        requiredStaff: shift.required_staff,
        notes: shift.notes,
      });
    } else {
      setFormData({
        name: '',
        startTime: '',
        endTime: '',
        requiredStaff: '',
        notes: '',
      });
    }
  }, [shift]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      if (shift) {
        await api.put(`/shifts/${shift.id}`, formData);
        toast.success('Schicht aktualisiert');
      } else {
        await api.post(`/events/${eventId}/shifts`, formData);
        toast.success('Schicht erstellt');
      }
      onSave();
      onClose();
    } catch (error) {
      toast.error('Fehler beim Speichern der Schicht');
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div>
      <h2>{shift ? 'Schicht bearbeiten' : 'Neue Schicht erstellen'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="datetime-local"
          name="startTime"
          placeholder="Startzeit"
          value={formData.startTime}
          onChange={handleChange}
          required
        />
        <input
          type="datetime-local"
          name="endTime"
          placeholder="Endzeit"
          value={formData.endTime}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="requiredStaff"
          placeholder="BenÃ¶tigte Mitarbeiter"
          value={formData.requiredStaff}
          onChange={handleChange}
          required
        />
        <textarea
          name="notes"
          placeholder="Notizen"
          value={formData.notes}
          onChange={handleChange}
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Speichert...' : 'Speichern'}
        </button>
        <button type="button" onClick={onClose}>
          Abbrechen
        </button>
      </form>
    </div>
  );
};

export default ShiftModal;
