import React, { useState } from 'react';
import { Outlet, NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import {
  HomeIcon,
  CalendarIcon,
  ClockIcon,
  BookOpenIcon,
  ChatBubbleLeftRightIcon,
  UserCircleIcon,
  UserGroupIcon,
  ClipboardDocumentListIcon,
  AcademicCapIcon,
  EnvelopeIcon,
  ChartBarIcon,
  Cog6ToothIcon,
  ArrowLeftOnRectangleIcon,
  Bars3Icon,
  XMarkIcon,
  TagIcon,
} from '@heroicons/react/24/outline';

const DashboardLayout = () => {
  const { user, logout, isAdmin } = useAuth();
  const navigate = useNavigate();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const navigation = [
    { name: 'Dashboard', href: '/dashboard', icon: HomeIcon },
    ...(isAdmin ? [] : [
      { name: 'Veranstaltungen', href: '/events', icon: CalendarIcon },
      { name: 'Mein Dienstplan', href: '/schedule', icon: ClipboardDocumentListIcon },
      { name: 'Stempeluhr', href: '/timestamp', icon: ClockIcon },
    ]),
    { name: 'Wissensdatenbank', href: '/knowledge', icon: BookOpenIcon },
    { name: 'Nachrichten', href: '/messages', icon: ChatBubbleLeftRightIcon },
  ];

  const adminNavigation = [
    { name: 'Bewerbungen', href: '/admin/applications', icon: ClipboardDocumentListIcon },
    { name: 'Mitarbeiter', href: '/admin/staff', icon: UserGroupIcon },
    { name: 'Veranstaltungen', href: '/admin/events', icon: CalendarIcon },
    { name: 'Qualifikationen', href: '/admin/qualifications', icon: AcademicCapIcon },
    { name: 'Positionen', href: '/admin/positions', icon: TagIcon },
    { name: 'E-Mail Vorlagen', href: '/admin/email-templates', icon: EnvelopeIcon },
    { name: 'Berichte', href: '/admin/reports', icon: ChartBarIcon },
    { name: 'Einstellungen', href: '/admin/settings', icon: Cog6ToothIcon },
  ];

  const userNavigation = [
    { name: 'Profil', href: '/profile', icon: UserCircleIcon },
    { name: 'Abmelden', action: handleLogout, icon: ArrowLeftOnRectangleIcon },
  ];

  const NavItem = ({ item }) => {
    if (item.action) {
      return (
        <button
          onClick={item.action}
          className="group flex items-center px-3 py-2 text-sm font-medium rounded-lg
                   text-ios-gray-700 hover:text-ios-gray-900 hover:bg-ios-gray-100
                   transition-colors duration-200"
        >
          <item.icon className="mr-3 h-5 w-5" aria-hidden="true" />
          {item.name}
        </button>
      );
    }

    return (
      <NavLink
        to={item.href}
        className={({ isActive }) =>
          `group flex items-center px-3 py-2 text-sm font-medium rounded-lg
           transition-colors duration-200 ${
             isActive
               ? 'bg-ios-blue text-white'
               : 'text-ios-gray-700 hover:text-ios-gray-900 hover:bg-ios-gray-100'
           }`
        }
      >
        <item.icon className="mr-3 h-5 w-5" aria-hidden="true" />
        {item.name}
      </NavLink>
    );
  };

  return (
    <div className="min-h-screen bg-ios-gray-100">
      {/* Mobile menu button */}
      <div className="lg:hidden fixed top-0 left-0 right-0 z-50 ios-nav-blur">
        <div className="flex items-center justify-between px-4 h-16">
          <button
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            className="p-2 rounded-lg text-ios-gray-700 hover:bg-ios-gray-100"
          >
            {mobileMenuOpen ? (
              <XMarkIcon className="h-6 w-6" />
            ) : (
              <Bars3Icon className="h-6 w-6" />
            )}
          </button>
          <h1 className="text-lg font-semibold">{process.env.REACT_APP_COMPANY_NAME}</h1>
          <div className="w-10" />
        </div>
      </div>

      {/* Sidebar */}
      <div className={`
        fixed inset-y-0 left-0 z-40 w-72 bg-white shadow-lg transform transition-transform duration-300 ease-in-out
        ${mobileMenuOpen ? 'translate-x-0' : '-translate-x-full'}
        lg:translate-x-0 lg:static lg:inset-auto lg:shadow-none
      `}>
        <div className="flex h-full flex-col">
          {/* Logo */}
          <div className="flex h-16 items-center px-6 border-b border-ios-gray-200">
            <h1 className="text-xl font-semibold text-ios-gray-900">
              {process.env.REACT_APP_COMPANY_NAME}
            </h1>
          </div>

          {/* Navigation */}
          <nav className="flex-1 space-y-1 px-3 py-4 overflow-y-auto">
            {/* Main Navigation */}
            <div>
              <h3 className="px-3 text-xs font-semibold text-ios-gray-500 uppercase tracking-wider">
                Hauptmenü
              </h3>
              <div className="mt-2 space-y-1">
                {navigation.map((item) => (
                  <NavItem key={item.name} item={item} />
                ))}
              </div>
            </div>

            {/* Admin Navigation */}
            {isAdmin && (
              <div className="pt-6">
                <h3 className="px-3 text-xs font-semibold text-ios-gray-500 uppercase tracking-wider">
                  Administration
                </h3>
                <div className="mt-2 space-y-1">
                  {adminNavigation.map((item) => (
                    <NavItem key={item.name} item={item} />
                  ))}
                </div>
              </div>
            )}

            {/* User Navigation */}
            <div className="pt-6">
              <h3 className="px-3 text-xs font-semibold text-ios-gray-500 uppercase tracking-wider">
                Benutzer
              </h3>
              <div className="mt-2 space-y-1">
                {userNavigation.map((item) => (
                  <NavItem key={item.name} item={item} />
                ))}
              </div>
            </div>
          </nav>

          {/* User info */}
          <div className="border-t border-ios-gray-200 p-4">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                {user?.profileImage ? (
                  <img
                    className="h-10 w-10 rounded-full object-cover"
                    src={`${process.env.REACT_APP_API_URL}${user.profileImage}`}
                    alt=""
                  />
                ) : (
                  <div className="h-10 w-10 rounded-full bg-ios-gray-300 flex items-center justify-center">
                    <UserCircleIcon className="h-6 w-6 text-ios-gray-600" />
                  </div>
                )}
              </div>
              <div className="ml-3">
                <p className="text-sm font-medium text-ios-gray-900">
                  {user?.firstName} {user?.lastName}
                </p>
                <p className="text-xs text-ios-gray-500">
                  {user?.role === 'admin' ? 'Administrator' : 'Mitarbeiter'}
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Mobile menu overlay */}
      {mobileMenuOpen && (
        <div
          className="fixed inset-0 z-30 bg-black/20 lg:hidden"
          onClick={() => setMobileMenuOpen(false)}
        />
      )}

      {/* Main content */}
      <div className="lg:pl-72">
        <main className="py-6 px-4 sm:px-6 lg:px-8 pt-20 lg:pt-6">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default DashboardLayout;
