// Protected Route Component
const ProtectedRoute = ({ children }) => {
  const { user } = useAuth();
  
  if (!user) {
    return <Navigate to="/login" replace />;
  }
  
  return children;
};

// Hauptkomponente
function App() {
  const { loading, user } = useAuth();

  if (loading) {
    return <LoadingScreen />;
  }

  return (
    <Routes>
      {/* Öffentliche Routen */}
      <Route path="/login" element={user ? <Navigate to="/dashboard" /> : <Login />} />
      
      {/* Geschützte Routen */}
      <Route path="/dashboard" element={
        <ProtectedRoute>
          <Dashboard />
        </ProtectedRoute>
      } />
      
      {/* Platzhalter für andere Seiten */}
      <Route path="/admin/applications" element={
        <ProtectedRoute>
          <PlaceholderPage title="Bewerbungsverwaltung" />
        </ProtectedRoute>
      } />
      
      <Route path="/admin/events" element={
        <ProtectedRoute>
          <PlaceholderPage title="Veranstaltungsverwaltung" />
        </ProtectedRoute>
      } />
      
      <Route path="/admin/staff" element={
        <ProtectedRoute>
          <PlaceholderPage title="Mitarbeiterverwaltung" />
        </ProtectedRoute>
      } />
      
      <Route path="/profile" element={
        <ProtectedRoute>
          <PlaceholderPage title="Mein Profil" />
        </ProtectedRoute>
      } />
      
      {/* Standard-Weiterleitung */}
      <Route path="/" element={<Navigate to="/dashboard" replace />} />
      
      {/* 404 */}
      <Route path="*" element={
        <div className="min-h-screen flex items-center justify-center">
          <div className="text-center">
            <h1 className="text-6xl font-bold text-gray-400">404</h1>
            <p className="mt-2 text-lg text-gray-600">Seite nicht gefunden</p>
            <a href="/" className="mt-4 inline-block bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600">
              Zur Startseite
            </a>
          </div>
        </div>
      } />
    </Routes>
  );
}

export default App;
