import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';

// Layouts
import DashboardLayout from './layouts/DashboardLayout';
import PublicLayout from './layouts/PublicLayout';

// Public Pages
import Login from './pages/public/Login';
import Apply from './pages/public/Apply';

// Dashboard Pages
import Dashboard from './pages/dashboard/Dashboard';

// Admin Pages
import Applications from './pages/admin/Applications';

// Components
import LoadingScreen from './components/common/LoadingScreen';
import ProtectedRoute from './components/common/ProtectedRoute';

// Placeholder Component fÃ¼r noch nicht implementierte Seiten
const PlaceholderPage = ({ title }) => (
  <div className="p-6">
    <h1 className="text-2xl font-bold text-ios-gray-900">{title}</h1>
    <p className="mt-2 text-ios-gray-600">Diese Seite wird noch entwickelt</p>
  </div>
);

function App() {
  const { loading, user } = useAuth();

  if (loading) {
    return <LoadingScreen />;
  }

  return (
    <Routes>
      {/* Public Routes */}
      <Route element={<PublicLayout />}>
        <Route path="/login" element={
          user ? <Navigate to="/dashboard" replace /> : <Login />
        } />
        <Route path="/apply" element={<Apply />} />
        <Route path="/reset-password" element={<PlaceholderPage title="Passwort zurÃ¼cksetzen" />} />
        <Route path="/set-password" element={<PlaceholderPage title="Passwort festlegen" />} />
      </Route>

      {/* Protected Routes */}
      <Route element={
        <ProtectedRoute>
          <DashboardLayout />
        </ProtectedRoute>
      }>
        {/* Common Dashboard Routes */}
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/profile" element={<PlaceholderPage title="Profil" />} />
        <Route path="/knowledge" element={<PlaceholderPage title="Wissensdatenbank" />} />
        <Route path="/messages" element={<PlaceholderPage title="Nachrichten" />} />

        {/* Staff Routes */}
        <Route path="/events" element={<PlaceholderPage title="Veranstaltungen" />} />
        <Route path="/events/:id" element={<PlaceholderPage title="Veranstaltungsdetails" />} />
        <Route path="/schedule" element={<PlaceholderPage title="Dienstplan" />} />
        <Route path="/timestamp" element={<PlaceholderPage title="Stempeluhr" />} />

        {/* Admin Routes */}
        <Route element={<ProtectedRoute requireAdmin />}>
          <Route path="/admin/applications" element={<Applications />} />
          <Route path="/admin/staff" element={<PlaceholderPage title="Mitarbeiterverwaltung" />} />
          <Route path="/admin/staff/:id" element={<PlaceholderPage title="Mitarbeiterdetails" />} />
          <Route path="/admin/events" element={<PlaceholderPage title="Veranstaltungsverwaltung" />} />
          <Route path="/admin/events/new" element={<PlaceholderPage title="Neue Veranstaltung" />} />
          <Route path="/admin/events/:id/edit" element={<PlaceholderPage title="Veranstaltung bearbeiten" />} />
          <Route path="/admin/qualifications" element={<PlaceholderPage title="Qualifikationen" />} />
          <Route path="/admin/positions" element={<PlaceholderPage title="Positionen" />} />
          <Route path="/admin/email-templates" element={<PlaceholderPage title="E-Mail Vorlagen" />} />
          <Route path="/admin/reports" element={<PlaceholderPage title="Berichte" />} />
          <Route path="/admin/settings" element={<PlaceholderPage title="Einstellungen" />} />
        </Route>
      </Route>

      {/* Kiosk Mode (Special Route) */}
      <Route path="/kiosk" element={
        <ProtectedRoute requireAdmin>
          <PlaceholderPage title="Kiosk-Modus" />
        </ProtectedRoute>
      } />

      {/* Default Redirect */}
      <Route path="/" element={<Navigate to="/dashboard" replace />} />
      
      {/* 404 Route */}
      <Route path="*" element={
        <div className="min-h-screen flex items-center justify-center">
          <div className="text-center">
            <h1 className="text-6xl font-bold text-ios-gray-400">404</h1>
            <p className="mt-2 text-lg text-ios-gray-600">Seite nicht gefunden</p>
            <a href="/" className="mt-4 inline-block ios-button-primary">
              Zur Startseite
            </a>
          </div>
        </div>
      } />
    </Routes>
  );
}

export default App;
